vector<LL>isPrime,primes;

inline void fastWrite(ull a) {
	char snum[50];
	int i = 0;
	do {
		snum[i++] = a % 10 + 48;
		a = a / 10;
	} while (a);
	i = i - 1;
	while (i >= 0)
		putchar(snum[i--]);
	cout<<endl;
}

ull getNum(string s){
	ull num = 0;
	for(int i=0;i<s.size();i++){
		num = num * 10 + (s[i] - '0');
	}
	return num;
}

//bitwise sieve faster version
void sieve(int n){
	isPrime.resize(n/64 + 5);
    unsigned i, j, k;
    for (i = 3; i*i <= n; i += 2){
        if (!chkC(isPrime, i))
        {    
            for (j = i * i, k = i << 1; j < n; j += k)
                setC(isPrime, j);
        }
    }
    primes.push_back(2);
    for (i = 3; i <= n; i += 2)
    {
        if (!chkC(isPrime, i))
            primes.push_back(i);
    }
}

/// Set your CPU's L1 data cache size (in bytes) here
//const LL L1D_CACHE_SIZE = 1000000;

inline ull segmented_sieve(LL start,LL lmt,ull l_sum,ull t_sum){
	vector<LL> multiple;  
    LL segment_size = 1000000;
    for(int i=0;i<primes.size();i++){
    	LL rem = start/primes[i];
    	if(rem&1)rem+=2;
    	else rem+=1;
    	multiple.push_back(primes[i]*rem - start);
	}
	
	for(u64 low = start+1,high; low <= lmt; low+=segment_size){
		high = (low + segment_size > lmt )?(lmt -(low-1)):segment_size;
    	fill(isPrime.begin(), isPrime.end(), 0);
		for(u64 i=0;i<primes.size();i++){
			u64 j = primes[i]*2,k;
			for ( k = multiple[i]; k <= high; k += j){
				if(k&1) setC(isPrime,k);
			}
			multiple[i] = k - high;	
		}
		for(u64 i=1;i<=high;i+=2){
			if(!chkC(isPrime,i)){
				l_sum+=(i+(low-1));
				if(l_sum >= t_sum){
					return (i + low - 1);
				}
			}
		}
	}
	return 0;
}

inline ull scan(){
    ull z=0;
	char c;
	do{ c=getchar(); } while(c<'0');
    for(;c>='0';c=getchar()) z = (z<<3) + (z<<1) + (c&15);
	return z;
}
int main(){
	ull n;
	sieve(1000001);
	n = scan();
    LL start = 2;
	LL limit = 300000000;
	ull t_sum = 1 ;
	ull l_sum = 0;
	t_sum = t_sum * n;
	if(n < 454396537){
		for(int i=0;i<primes.size();i++){
			l_sum+=primes[i];
			if(l_sum >= n){
				cout << primes[i];
				break;
			}
			
		}
		
	}else{
	for(int i=0;i<1008;i++){
		if( getNum(prime_sum[i][1] ) >= n && n >= 454396537){
			start = getNum(prime_sum[i-1][0]);
			l_sum = getNum(prime_sum[i-1][1]);
			limit = getNum(prime_sum[i][0]);
			t_sum = n;
			break;
		}
	}
	fastWrite(segmented_sieve(start,limit,l_sum,t_sum))	;	
	}
}
